(function() {
function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms))
}

function start_scroll() {
  let pixel_per_sec = 30 / 1000
  let down = true
  let target = null, start_y
  let start, duration, diff

  async function step(timestamp) {
    if (target == null) {
      target = down ? document.body.scrollHeight - window.innerHeight : 0
      start_y = document.documentElement.scrollTop
      diff = target - start_y
      start = timestamp
      duration = Math.abs(diff) / pixel_per_sec
      // console.log('target now', target, start, diff, start_y, duration)
    }
    let time = timestamp - start
    let percent = Math.min(time / duration, 1)
    // console.log('percent', percent, start_y + diff * percent)
    window.scrollTo({top: start_y + diff * percent})
    if (percent == 1) {
      await sleep(3000)
      down = !down
      target = null
    }
    window.requestAnimationFrame(step)
  }
  window.requestAnimationFrame(step)
}

let stateCheck = setInterval(async () => {
  if (document.readyState === 'complete' && !document.hidden) {
    clearInterval(stateCheck)
    await sleep(3000)
    start_scroll()
  }
}, 100)

})();
